hudson.tasks.Shell.command:
#!/usr/bin/env bash
LOCAL_SCRIPT_PATH=$(eval echo -n ${LOCAL_SCRIPT_PATH})
function db_query() { [ "$1" = "" ] && return local sql=$1 local query_rst=$(python3 -c """ import os os.chdir('$LOCAL_SCRIPT_PATH') import utils,mysql.connector db_connect = mysql.connector.connect( host=utils.DB_HOST,port=utils.DB_PORT, user=utils.DB_USER,passwd=utils.DB_PASSWORD, database=utils.DB_DATABASE) cursor = db_connect.cursor() cursor.execute('$sql') result = cursor.fetchall() print(result) if len(result) > 0 else None cursor.close() db_connect.close()""") query_rst=${query_rst//[/} query_rst=${query_rst//]/} query_rst=${query_rst//\'/} query_rst=${query_rst//, /,} query_rst=${query_rst//\),\(/\) \(} echo $query_rst }
function db_execute() { [ "$1" = "" ] && return local sql=$1 local query_rst=$(python3 -c """ import os os.chdir('$LOCAL_SCRIPT_PATH') import utils,mysql.connector db_connect = mysql.connector.connect( host=utils.DB_HOST,port=utils.DB_PORT, user=utils.DB_USER,passwd=utils.DB_PASSWORD, database=utils.DB_DATABASE) cursor = db_connect.cursor() cursor.execute('$sql') db_connect.commit() print(cursor.rowcount) cursor.close() db_connect.close()""") echo $query_rst }
function ssh_execute() { [[ "$1" = "" || "$2" = "" || "$3" = "" || "$4" = "" ]] && return local ip=$1 local user=$2 local pwd=$3 local cmd=$4
sshpass -p "${pwd}" ssh -o StrictHostKeyChecking=no "${user}@${ip}" "eval ${cmd}" }
function check_host_status() { [ "$1" = "" ] && return local id=$1 local rst=$(db_query "select server_status from devops_server where id=$id") rst=${rst//\(/} echo $rst | awk -F, '{print $1}' }
function cmd_base() { echo """ # base cmd [ ! -d $REMOTE_SCRIPT_PATH ] && exit; cd $REMOTE_SCRIPT_PATH; echo -n 'now in '; ip a | grep \"\(192.168\)\|\(10.20\)\" | awk '{print \$2}' | awk -F/ '{print \$1}';
function clean_overdue() { [ -n \"\$1\" ] && files=\"\$1\" || return [ -n \"\$2\" ] && delay=\"\$2\" || delay=7
now=\`date +%s\` overdue=\`expr \$now - \$delay \\* 24 \\* 60 \\* 60\` for f in \$files; do t=\`date +%s -r \$f\` echo -n \$f\(\$t\) [ \$t -lt \$overdue ] && echo ' overdue' && rm -rf \$f || echo; done }
function print_location() { echo echo -n 'now in '; pwd; } """ }
function cmd_clean_script() { echo """ cd $REMOTE_SCRIPT_PATH print_location # clean tag file 7 days ago echo -e \"\nclean manifest tag files\" tag_files=\`find -type f -regex '^./tag_[0-9]*_[0-9]*.xml'\` clean_overdue \"\$tag_files\" \"7\"
# clean script echo -e \"\nclean script files\" script_files=\`find -maxdepth 1 -type f -regex '.*\.sh\|.*\.py'\` clean_overdue \"\$script_files\" \"0\" || echo """ }
function cmd_clean_code() { [ "$1" = "" ] && return echo """ # clean code codes=\"$1\"; for code in \${codes[@]}; do code=\${code//,/ }; code=\${code//(/}; code=\${code//)/}; code=(\$code); [ ! -d \${code[0]} ] && continue; cd \${code[0]}; print_location script_path=\$(eval echo -n $REMOTE_SCRIPT_PATH)
# clean jenkins link file echo -e \"\nclean script link files\" script_links=\$(for i in \`find -maxdepth 1 -type l\`; do l=\`ls -l \$i | awk '{print \$NF}'\`; echo \$l | grep \$script_path > /dev/null && echo \$i; done) clean_overdue \"\$script_links\" \"0\"
# clean log file 3 days ago echo -e \"\nclean log files\" log_files=\`find -maxdepth 1 -type f -name \"*.log\"\` clean_overdue \"\$log_files\" \"3\"
# clean publish files 7 days ago echo -e \"\nclean publish files\" publish_files=\`[ -d droi/out ] && find droi/out -mindepth 1 -maxdepth 1\` clean_overdue \"\$publish_files\" \"7\" done cd $REMOTE_SCRIPT_PATH; """ }
function clean() { [ "$1" = "" ] && return local compile_hosts=$1 for host in ${compile_hosts[@]}; do host=${host//,/ } host=${host//(/} host=${host//)/} host=($host) status=$(check_host_status ${host[0]}) echo "id: ${host[0]}, ip: ${host[1]}, user: ${host[2]}, status: $status"
if [[ $status == 0 ]]; then # mark server shutdown first db_execute "update devops_server set server_status=2 where id=${host[0]}"
# generate remote cmd local cmd="$(cmd_base)" # dump codes local codes=$(db_query "select code_dir from devops_code where code_server_id=${host[0]} and code_status=2") cmd="$cmd""$(cmd_clean_code "$codes")" cmd="$cmd""$(cmd_clean_script)"
# execute by ssh ssh_execute "${host[1]}" "${host[2]}" "${host[3]}" "$cmd"
# mark server normal db_execute "update devops_server set server_status=0 where id=${host[0]}" fi done }
# main, start...
COMPILE_HOSTS=$(db_query 'select id,server_ip,server_host,server_password from devops_server where server_use_flag="compile"') # COMPILE_HOSTS=$(db_query 'select id,server_ip,server_host,server_password from devops_server where id in (1412653247472128001,1410057686470430722)')
# clean superfluous manifest tag clean "$COMPILE_HOSTS"
